var data = "Hi Team, I know the times are difficult! Our sales have been disappointing for the past three quarters for our data analytics product suite. We have a competitive data analytics product suite in the industry. But we need to do our job selling it!";
msg.payload=data;
return msg;

// Set the content type to audio wave
var wavFileName=msg.text_to_say.replace(' ','_');
if(wavFileName.length>32) {
    wavFileName=wavFileName.substr(0,32);
}
var attch='attachment; filename='+encodeURIComponent(wavFileName+'.wav');
msg.headers={ 'Content-Type': 'audio/wav',
              'Content-Disposition': attch};

return msg;

msg = {
    payload: 'https://www.ibm.com/developerworks/community/blogs/efc1d8f5-72e5-4c4f-99df-e74fccea10ca/resource/White%20Papers/IBMWatsonNextGenerationofCognitiveSystemswhitepaper.pdf?lang=en',
    pdf: {
        "heading": {
            "fonts": [
                {"level": 1, "min_size": 24, "max_size": 80},
                {"level": 2, "min_size": 18, "max_size": 24, "bold": false, "italic": false},
                {"level": 2, "min_size": 18, "max_size": 24, "bold": true},
                {"level": 3, "min_size": 13, "max_size": 18, "bold": false, "italic": false},
                {"level": 3, "min_size": 13, "max_size": 18, "bold": true},
                {"level": 4, "min_size": 11, "max_size": 13, "bold": true, "italic": false}
            ]
        }
    }
}
return msg;

msg.params={};
msg.params['accept_language']='ar'; // try en, jp, es, ar
return msg;

// stash away incoming data
msg.mydata = {};
msg.mydata.messagein = msg.req.body.msgdata;
msg.payload = msg.mydata.messagein;

msg.params = { "context": msg.req.body.context};

return msg;

msg.mydata.messageout = msg.payload;

msg.payload = {};
msg.payload.botresponse = msg.mydata;

return msg;
